import { useSelector, useDispatch } from 'react-redux';
import ProductItem from './product-item';

import * as productActions from '../store/actions/products';


const Products = () => {
    const dispatch = useDispatch();

    const products = useSelector(state => state.products.availableProducts);
    const searchProducts = useSelector(state => state.products.searchProducts);

    // console.log(products);
    const filteredProduct = products.filter(product => console.log(product.productId));
    // const filteredProduct = products.filter(product => product.productName.includes(searchProducts.toLowerCase()));

    return (
        <div>
            <div style={{ flex: 1, margin: 20 }}>
                <input
                    type="text"
                    placeholder="Cari aja dulu..."
                    value={searchProducts}
                    onChange={event => dispatch(productActions.searchProducts(event.target.value))}
                />
                <> Filter by Category: </>
                <select>
                    <option value="">Category</option>
                </select>
            </div>
            {
                products.map(item => <ProductItem
                    key={item.productId}
                    id={item.productId}
                    image={item.productImage}
                    name={item.productName}
                    price={item.productPrice}
                    description={item.productDescription}
                />)
            }
        </div>

    )
}

export default Products;
