import { useSelector } from 'react-redux';

import Layout from '../components/layout';
import Header from '../components/header';

const CartList = ({ image, name, price, quantity, sum }) => (
    <div>
        <h4>{name}</h4>
        <p>Price : {price}</p>
        <p>Quantity: {quantity}</p>
        <p>Total: {sum}</p>
        <button>Delete This Item</button>
    </div>
)

const Cart = () => {
    const cartItems = useSelector(state => {
        const transformedCartItems = [];
        for (const key in state.cart.items) {
            transformedCartItems.push({
                productId: key,
                productName: state.cart.items[key].productName,
                productPrice: state.cart.items[key].productPrice,
                quantity: state.cart.items[key].quantity,
                sum: state.cart.items[key].sum
            });
        }
        return transformedCartItems;
    });

    const totalAmout = useSelector(state => state.cart.totalAmout);
    return (
        <Layout>
            <p>Cart page</p>
            <Header />
            <h1>Total Amount: {totalAmout}</h1>
            {
                cartItems.map(item => <CartList
                    key={item.productId}
                    image={item.productImage}
                    name={item.productName}
                    price={item.productPrice}
                    quantity={item.quantity}
                    sum={item.sum}
                />)
            }
        </Layout>
    )
}


export default Cart;
