import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import ProductItem from './product-item';

import * as productActions from '../store/actions/products';


const Products = () => {
    const dispatch = useDispatch();

    const products = useSelector(state => state.products.availableProducts);
    let searchProducts;
    useEffect(() => {
        // Update the document title using the browser API
        // document.title = `You clicked ${count} times`;
        searchProducts = useSelector(state => state.products.searchProducts);
        console.log(searchProducts);
    });


    const filteredProduct = products.filter(product => product.productName.toLowerCase().includes(searchProducts.toLowerCase()));

    return (
        <div>
            <div style={{ flex: 1, margin: 20 }}>
                <input
                    type='search'
                    placeholder="Cari aja dulu..."
                    // value={searchProducts}
                    onChange={e => {
                        // console.log(e.target.value)
                        dispatch(productActions.searchProducts(e.target.value))
                    }}
                />
                <> Filter by Category: </>
                <select>
                    <option value="">Category</option>
                </select>
            </div>
            {
                products.map(item => <ProductItem
                    key={item.productId}
                    id={item.productId}
                    image={item.productImage}
                    name={item.productName}
                    price={item.productPrice}
                    description={item.productDescription}
                />)
            }
        </div>

    )
}

export default Products;
