import { ADD_TO_CART } from "../actions/cart";
import CartItem from '../../models/product';
import { createReducer } from '@reduxjs/toolkit';

const initialState = {
    items: {}
};

const cartReducer = createReducer(initialState, {
    [ADD_TO_CART]: (state, action) => {
        const addedProduct = action.payload;
        const prodId = addedProduct.productId;
        const prodPrice = addedProduct.productPrice;
        const prodName = addedProduct.productName;

        console.log(addedProduct)
        let updatedOrNewCartItem;

        if (state.items[addedProduct.productId]) {
            updatedOrNewCartItem = new CartItem(
                prodId,
                prodName,
                prodPrice,
                state.items[addedProduct.prodId].quantity + 1,
            );
        } else {
            updatedOrNewCartItem = new CartItem(prodId, prodName, prodPrice, 1);
        }

        return {
            ...state,
            items: { ...state.items, [prodId]: updatedOrNewCartItem },
        };
    }
})

export default cartReducer;
