import { useRouter } from 'next/router';
import { useSelector, useDispatch } from 'react-redux';


import Layout from '../../components/layout';
import Header from '../../components/header';

const Post = props => {
    const dispatch = useDispatch();

    return (
        <Layout>
            <p>Product Detail Page</p>
            <Header />

            <img src={props.productImage} />
            <h1>{props.productName}</h1>
            <h3>{props.productPrice}</h3>
            <p>{props.productDescription}</p>
            <button onClick={() => { dispatch(wishListActions.addToWishList(props)) }}>Add To Wishlist</button>
            <button onClick={() => { dispatch(cartActions.addToCart(props)) }}>Add To Cart</button>
        </Layout>
    )
};

Post.getInitialProps = async function (context) {
    const { id } = context.query;
    // const router = useRouter();
    const selectedProduct = useSelector(state => state.products.availableProducts[id]);

    console.log(selectedProduct);

    return { selectedProduct };
};

export default Post;
