import { useSelector, useDispatch, usee } from 'react-redux';
import ProductItem from './product-item';

import * as productActions from '../store/actions/products';


const Products = (props) => {
    const dispatch = useDispatch();

    const products = useSelector(state => state.products.availableProducts);
    // const searchProducts = useSelector(state => state.products.searchProducts);

    // console.log(searchProducts);
    const filteredProduct = products.filter(product => product.productName.toLowerCase().includes(props.searchProducts.toLowerCase()));

    return (
        <div>
            <div style={{ flex: 1, margin: 20 }}>
                <input
                    type='search'
                    placeholder="Cari aja dulu..."
                    // value={searchProducts}
                    onChange={e => {
                        // console.log(e.target.value)
                        dispatch(productActions.searchProducts(e.target.value))
                    }}
                />
                <> Filter by Category: </>
                <select>
                    <option value="">Category</option>
                </select>
            </div>
            {
                filteredProduct.map(item => <ProductItem
                    key={item.productId}
                    id={item.productId}
                    image={item.productImage}
                    name={item.productName}
                    price={item.productPrice}
                    description={item.productDescription}
                />)
            }
        </div>

    )
}

Products.getInitialProps = () => {
    const searchProducts = useSelector(state => state.products.searchProducts);

    console.log(searchProducts);
    return { searchProducts };
};

export default Products;
