// import { ADD_TO_CART } from "../actions/cart";

const ADD_TO_CART = createAction('ADD_TO_CART')

const initialState = {
    items: 0
};

const cartReducer = (state, action) => {
    if (typeof state === 'undefined') {
        return 0
    }

    switch (action.type) {
        case 'INCREMENT':
            return state + 1
        case 'DECREMENT':
            return state - 1
        default:
            return state
    }
}

